    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        //System.out.println(tf_nim.getText());
        P2_data dtMhs = new P2_data();
        dtMhs.setNim(tf_nim.getText());
        dtMhs.setNama(tf_nama.getText());
        dtMhs.setN_uts(Double.parseDouble(tf_uts.getText()));
        dtMhs.setN_uas(Double.parseDouble(tf_uas.getText()));
        
        System.out.println(dtMhs.hitungNilaiAkhir());
        tf_akhir.setText(Double.toString(dtMhs.hitungNilaiAkhir()));
        tf_grade.setText(String.valueOf(dtMhs.hitungGrade()));
        
        tf_grade.setEditable(false);
        tf_grade.setPreferredSize(new java.awt.Dimension(128, 22));
    }//GEN-LAST:event_jButton1ActionPerformed

